import 'package:cloud_firestore/cloud_firestore.dart';import 'package:json_annotation/json_annotation.dart';part 'recruit_con_md_req.g.dart';class TimestampConverter implements JsonConverter<Timestamp, dynamic> {  const TimestampConverter();  @override  Timestamp fromJson(dynamic json) {    return Timestamp.fromMillisecondsSinceEpoch(json.millisecondsSinceEpoch);  }  @override  dynamic toJson(Timestamp timestamp) {    return timestamp.millisecondsSinceEpoch;  }}@JsonSerializable(anyMap: true)class RecruitConModelReq {  String? education;  String? personnel;  String? gender;  String? age;  @TimestampConverter()  Timestamp? deadline;  @override  RecruitConModelReq({    this.education,    this.personnel,    this.deadline,    this.gender,    this.age,  });  factory RecruitConModelReq.fromJson(Map json) =>      _$RecruitConModelReqFromJson(json);  Map toJson() => _$RecruitConModelReqToJson(this);}