import '../../../presentation/template/template.dart';import '../../models/Address/address_md.dart';import '../../models/Job/job_md.dart';import '../../models/User/user_md.dart';import '../../models/User/user_profile_md.dart';import '../../models/resume/education_md.dart';import '../../models/resume/experience_md.dart';import '../../models/resume/skill_md.dart';@immutableclass UserState {  final UserModel userData;  final UserProfileModel userProfileData;  final List<JobModel> mySavedJobsJobData;  const UserState({    required this.userData,    required this.userProfileData,    required this.mySavedJobsJobData,  });  factory UserState.initial() {    return UserState(      userData: UserModel(        userId: "",        userProfileId: "",        isAdmin: false,        phoneNumber: "",        password: "",      ),      userProfileData: UserProfileModel(        userId: "",        userProfileId: "",        firstName: "",        lastName: "",        createdDate: "",        email: "",        contactNumber: "",        address: AddressModel(          division: "",          district: "",          city: "",          area: "",        ),        profileImage: "",        myJobsIds: [],        education: [],        experience: [],        skill: [],        birthday: "",        positionTitle: "",        bio: "",      ),      mySavedJobsJobData: [],    );  }  UserState copyWith({    UserModel? userData,    UserProfileModel? userProfileData,    List<JobModel>? mySavedJobsJobData,  }) {    return UserState(      userData: userData ?? this.userData,      userProfileData: userProfileData ?? this.userProfileData,      mySavedJobsJobData: mySavedJobsJobData ?? this.mySavedJobsJobData,    );  }}///------------------Update User Action --------- ///class UpdateUserStateAction {  final UserModel? userData;  final UserProfileModel? userProfileData;  final List<JobModel>? mySavedJobsJobData;  UpdateUserStateAction({    this.userData,    this.userProfileData,    this.mySavedJobsJobData,  });}///------------------User Action --------- ///class GetUpdateAccountAction {  UserModel userData;  GetUpdateAccountAction({required this.userData});}class GetUpdateProfileAction {  UserProfileModel userProfileData;  GetUpdateProfileAction({required this.userProfileData});}class GetUserProfileAction {  String userId;  String userProfileId;  GetUserProfileAction({required this.userId, required this.userProfileId});}class GetUpdateSavedJobsAction {  JobModel jobData;  GetUpdateSavedJobsAction({required this.jobData});}class GetAllMySavedJobsAction {  GetAllMySavedJobsAction();}class GetLocalUserIdAction {}class SetLocalUserIdAction {  String userId;  SetLocalUserIdAction(this.userId);}