import 'dart:convert';import 'dart:io';import 'package:auto_route/annotations.dart';import 'package:auto_route/auto_route.dart';import 'package:cloud_firestore/cloud_firestore.dart';import 'package:flutter/foundation.dart';import 'package:flutter_multi_formatter/formatters/masked_input_formatter.dart';import 'package:flutter_quill/flutter_quill.dart';import 'package:intl/date_symbol_data_local.dart';import 'package:intl/intl.dart';import 'package:mehonot_admin/presentation/utils/constants.dart';import '../../../../manager/models/Ads/banner_ads/banner_md.dart';import '../../../../manager/navigation/router.gr.dart';import '../../../../manager/redux/states/ads_state.dart';import '../../../template/template.dart';import '../../../utils/common/validators.dart';import '../../../widgets/photo_widgets/image_upload.dart';@RoutePage()class BannerCreatePage extends StatefulWidget {  final String bannerType;  const BannerCreatePage({    Key? key,    required this.bannerType,  }) : super(key: key);  @override  State<BannerCreatePage> createState() => _BannerCreatePageState();}class _BannerCreatePageState extends State<BannerCreatePage> {  final GlobalKey<FormState> createBannerFormKey = GlobalKey<FormState>();  TextEditingController bannerTitleCntr =      TextEditingController(text: kDebugMode ? "Test Debugging" : "");  TextEditingController bannerDescCntr = TextEditingController();  TextEditingController companyCntr =      TextEditingController(text: kDebugMode ? "Comp Test Corp." : "");  TextEditingController emailCntr =      TextEditingController(text: kDebugMode ? "test@test.com" : "");  TextEditingController phoneCntr =      TextEditingController(text: kDebugMode ? "01097802701" : "");  TextEditingController startDateCntr = TextEditingController();  TextEditingController endDateCntr = TextEditingController();  Timestamp createBannerStartDate = Timestamp.now();  Timestamp createBannerEndDate = Timestamp.now();  late String _bannerType;  late String _bannerDivision;  bool jobIsCreate = false;  File bannerImage = File("");  QuillController _quilCntr = QuillController.basic();  String quilStringToUpload = "";  @override  void dispose() {    bannerTitleCntr.dispose();    bannerDescCntr.dispose();    companyCntr.dispose();    emailCntr.dispose();    phoneCntr.dispose();    super.dispose();  }  @override  void initState() {    super.initState();    _bannerType = widget.bannerType;    _bannerDivision = Constants.jobDivisionList.first.name;  }  @override  Widget build(BuildContext context) {    return Form(        key: createBannerFormKey,        child: Padding(          padding: EdgeInsets.all(40.w),          child: SingleChildScrollView(              child: SpacedColumn(verticalSpace: 40, children: [            _buildTitleSec(),            _buildImageSec(),            _buildCompanyNameSec(),            _buildEmailSec(),            _buildPhoneSec(),            _buildDurationSec(), // _buildDescSec(),            _buildQuillDescSec(),            _buildBannerDivisionSec(),            _buildBannerTypeSec(),            _buildButtonSec(context)          ])),        ));  }  Widget _buildTitleSec() {    return SpacedColumn(children: [      Align(          alignment: Alignment.centerLeft,          child: SizedText(              text: S(context).title, textStyle: ThemeTextSemiBold.k14)),      PrsmInputField(        controller: bannerTitleCntr,        maxLines: 5,        enableShadow: false,        validator: Validator(context).validateField,        defaultBorderColor: ThemeColors.coolgray300,      ),    ]);  }  Widget _buildImageSec() {    return SpacedColumn(children: [      divider(),      PrsmImageUpload(        maxImageCount: 1,        onImageSelected: (imageFile, image) {          logger("Image Selected");          logger(imageFile);          logger(image);          setState(() {            bannerImage = imageFile[0];          });        },        onRemoveNetworkImg: (int) {},        imageNetUrls: [],      ),      divider(),    ]);  }  Widget _buildCompanyNameSec() {    return SpacedColumn(children: [      Align(          alignment: Alignment.centerLeft,          child: SizedText(              text: "Company Name", textStyle: ThemeTextSemiBold.k14)),      SizedBox(          width: MediaQuery.of(context).size.width - 100.w,          child: PrsmInputField(            controller: companyCntr,            validator: Validator(context).validateField,          ))    ]);  }  Widget _buildEmailSec() {    return SpacedColumn(children: [      Align(          alignment: Alignment.centerLeft,          child: SizedText(text: "Email", textStyle: ThemeTextSemiBold.k14)),      SizedBox(          width: MediaQuery.of(context).size.width - 100.w,          child: PrsmInputField(            controller: emailCntr,            validator: Validator(context).validateField,          ))    ]);  }  Widget _buildPhoneSec() {    return SpacedColumn(children: [      Align(          alignment: Alignment.centerLeft,          child: SizedText(              text: "Phone number", textStyle: ThemeTextSemiBold.k14)),      SizedBox(          width: MediaQuery.of(context).size.width - 100.w,          child: PrsmInputField(            controller: phoneCntr,            keyboardType: TextInputType.number,            formatter: [              MaskedInputFormatter('00000000000',                  allowedCharMatcher: RegExp(r'[0-9]'))            ],            validator: Validator(context).validatePhone,          ))    ]);  }  Widget _buildDurationSec() {    return SpacedColumn(children: [      Align(          alignment: Alignment.centerLeft,          child: SizedText(text: "Duration", textStyle: ThemeTextSemiBold.k14)),      SpacedRow(          crossAxisAlignment: CrossAxisAlignment.center,          mainAxisAlignment: MainAxisAlignment.spaceBetween,          children: [            SizedBox(              width: (MediaQuery.of(context).size.width / 2) - 80.w,              child: PrsmInputField(                enabled: false,                controller: startDateCntr,                hintText: "Start date",                onTap: () => showDateCalendarPopup(context, true),              ),            ),            SizedBox(              width: (MediaQuery.of(context).size.width / 2) - 80.w,              child: PrsmInputField(                enabled: false,                controller: endDateCntr,                hintText: "End date",                onTap: () => showDateCalendarPopup(context, false),              ),            )          ])    ]);  }  Widget _buildDescSec() {    return SpacedColumn(children: [      Align(          alignment: Alignment.centerLeft,          child:              SizedText(text: "Description", textStyle: ThemeTextSemiBold.k14)),      PrsmInputField(        controller: bannerDescCntr,        maxLines: 5,        enableShadow: false,        validator: Validator(context).validateField,        defaultBorderColor: ThemeColors.coolgray300,      ),    ]);  }  Widget _buildQuillDescSec() {    return SpacedColumn(children: [      Padding(          padding: EdgeInsets.symmetric(vertical: 20.h),          child: SpacedRow(            crossAxisAlignment: CrossAxisAlignment.center,            mainAxisAlignment: MainAxisAlignment.spaceBetween,            children: [              SizedText(text: "Description", textStyle: ThemeTextSemiBold.k14),              if (!_quilCntr.document.isEmpty())                InkWell(                  onTap: quillTextEditor,                  child: const HeroIcon(                    HeroIcons.pencilSquare,                    color: ThemeColors.green900,                  ),                )            ],          )),      if (_quilCntr.document.isEmpty())        PrimaryButton(            buttonText: "Create Description",            bgColor: ThemeColors.emerald500,            btnTextStyle: ThemeTextSemiBold.k18.copyWith(color: Colors.white),            onPressed: quillTextEditor)      else        Container(            decoration: BoxDecoration(              border: Border.all(color: ThemeColors.coolgray300, width: 1.w),              borderRadius: BorderRadius.circular(18.w),              color: ThemeColors.blue50,            ),            padding: EdgeInsets.all(40.w),            width: MediaQuery.of(context).size.width - 100.w,            height: 400.h,            child: QuillEditor(              controller: _quilCntr,              readOnly: true,              focusNode: FocusNode(),              scrollController: ScrollController(),              scrollable: true,              padding: EdgeInsets.zero,              autoFocus: false,              expands: true,              showCursor: false,            ))    ]);  }  Widget _buildBannerDivisionSec() {    return SpacedColumn(children: [      Align(          alignment: Alignment.centerLeft,          child: SizedText(              text: "Banner division", textStyle: ThemeTextSemiBold.k14)),      SizedBox(          width: MediaQuery.of(context).size.width - 100.w,          child: PrsmDropdown(              listValues: Constants.jobDivisionList.map((e) => e.name).toList(),              value: _bannerDivision,              onChanged: (val) {                setState(() {                  _bannerDivision = val;                });              }))    ]);  }  Widget _buildBannerTypeSec() {    return SpacedColumn(children: [      Align(          alignment: Alignment.centerLeft,          child: SizedText(              text: "Banner choice", textStyle: ThemeTextSemiBold.k14)),      SizedBox(          width: MediaQuery.of(context).size.width - 100.w,          child: PrsmDropdown(              listValues: const ["home", "setting"],              value: _bannerType,              onChanged: (val) {                setState(() {                  _bannerType = val;                });              }))    ]);  }  Widget _buildButtonSec(BuildContext context) {    return SpacedColumn(children: [      divider(),      PrimaryButton(          buttonText: S(context).post,          buttonSize: ButtonSize.L,          onPressed: () async {            if (createBannerFormKey.currentState!.validate()) {              showConfirmJobPostPopup(context, false);            } else {              logger("Validation Error");            }          }),    ]);  }  Widget divider() {    return const Divider(      color: ThemeColors.coolgray300,    );  }  void showConfirmJobPostPopup(BuildContext context, bool? updateJob) {    showDialog(        context: context,        barrierDismissible: false,        builder: (BuildContext context) {          return AlertDialog(            title: const SizedText(text: "Confirm Banner post"),            content: const SizedText(                text: "Are you sure you want to post this banner?"),            actions: [              TextButton(                  onPressed: () {                    Navigator.pop(context);                  },                  child: SizedText(text: S(context).cancel)),              TextButton(                  onPressed: () {                    createBanner();                    Navigator.pop(context);                  },                  child: SizedText(text: S(context).confirm)),            ],          );        });  }  showDateCalendarPopup(BuildContext context, bool startDate) async {    try {      final DateTime? picked = await showDatePicker(        context: context,        initialDate: DateTime.now(),        firstDate: DateTime.now(),        lastDate: DateTime(2101),      );      if (picked != null) {        setState(() {          if (startDate) {            createBannerStartDate = Timestamp.fromDate(picked);            startDateCntr.text = DateFormat('yyyy-MM-dd').format(picked);          } else {            createBannerEndDate = Timestamp.fromDate(picked);            endDateCntr.text = DateFormat('yyyy-MM-dd').format(picked);          }        });      }    } catch (e) {      print('Error formatting date: $e');    }  }  createBanner() async {    bool success = await appStore.dispatch(GetCreateBannersAction(        division: convertStringToDivision(_bannerDivision),        context: context,        bannerImg: bannerImage,        bannerType: _bannerType,        bannerModel: BannerModel(          title: bannerTitleCntr.text,          description: quilStringToUpload,          companyName: companyCntr.text,          email: emailCntr.text,          bannerType: _bannerType,          image: "",          id: "",          postedById: appStore.state.userState.userProfileData.userId,          phone: phoneCntr.text,          createdAt: Timestamp.now(),          startAt: createBannerStartDate,          removeAt: createBannerEndDate,        )));    if (success) {      if (context.mounted) {        showDialog(            barrierDismissible: false,            context: context,            builder: (BuildContext context) {              return AlertDialog(                title: const SizedText(text: "Success"),                content: const SizedText(text: "Banner created successfully"),                actions: [                  TextButton(                      onPressed: () async {                        await appStore.dispatch(GetHomeBannersAction());                        await appStore.dispatch(GetSettingsBannersAction());                        if (context.mounted) {                          Navigator.pop(context);                          context.navigateTo(const BannerHomeListRoute());                        }                      },                      child: const SizedText(text: "Ok"))                ],              );            });      }    }  }  void quillTextEditor() {    context.pushRoute(QuillTestRoute(        controller: _quilCntr,        onDone: (quilTextCntr) {          logger(quilTextCntr, hint: "Quill Text Cntr");          logger(quillControllerToJsonString(quilTextCntr),              hint: "Quill Text Cntr1");          context.popRoute();          setState(() {            _quilCntr = quilTextCntr;          });          quillControllerToJsonString(quilTextCntr);        }));  }  quillControllerToJsonString(QuillController quillController) {    Document document = quillController.document;    Delta delta = document.toDelta();    List<dynamic> jsonList = delta.toList();    String jsonString = json.encode(jsonList);    setState(() {      quilStringToUpload = jsonString;    });  }}