import 'package:auto_route/auto_route.dart';import 'package:mehonot_admin/manager/models/Job/job_md.dart';import 'package:mehonot_admin/manager/navigation/router.gr.dart';import 'package:mehonot_admin/manager/redux/states/ads_state.dart';import '../../../template/template.dart';import '../../../utils/constants.dart';@RoutePage()class JobAdsListPage extends StatefulWidget {  const JobAdsListPage({Key? key}) : super(key: key);  @override  State<JobAdsListPage> createState() => _JobAdsListPageState();}class _JobAdsListPageState extends State<JobAdsListPage> {  Division selectedDivision = Division.Dhaka;  List<JobModel> jobList = [];  @override  Widget build(BuildContext context) {    return StoreConnector<AppState, AppState>(        converter: (store) => store.state,        builder: (_, state) {          return SizedBox(              height: MediaQuery.of(context).size.height - 300,              child: Padding(                  padding: EdgeInsets.all(20.w),                  child: SingleChildScrollView(                      child: SpacedColumn(                          crossAxisAlignment: CrossAxisAlignment.start,                          children: [                        _buildTopSec(),                        _buildJobAdsList(state)                      ]))));        });  }  _buildTopSec() {    return SpacedRow(        crossAxisAlignment: CrossAxisAlignment.center,        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          SizedBox(              width: 500.w,              child: PrsmDropdown(                  listValues:                      Constants.jobDivisionList.map((e) => e.name).toList(),                  value: selectedDivision.name,                  onChanged: (value) {                    setState(() {                      selectedDivision = Constants.jobDivisionList                          .firstWhere((element) => element.name == value);                    });                  })),          SizedBox(              width: 500.w,              child: PrimaryButton(                  buttonText: "Create Job Ad",                  btnTextStyle: ThemeTextRegular.k12,                  linkTypeBtnColor: ThemeColors.blue800,                  onPressed: () {                    context.pushRoute(const JobAdCreateRoute());                  })),        ]);  }  _buildJobAdsList(AppState state) {    List<Widget> listWidget = [];    for (int i = 0; i < jobList.length; i++) {      listWidget.add(PrsmJobContainer(          jobModel: jobList[i],          onTap: () {            context.pushRoute(JobDetailsRouter(jobModel: jobList[i]));          }));    }    return SpacedColumn(      verticalSpace: 20,      children: listWidget,    );  }}