import 'package:auto_route/auto_route.dart';import 'package:lottie/lottie.dart';import '../../../manager/navigation/router.gr.dart';import '../../template/template.dart';@RoutePage()class RegDone01Page extends StatefulWidget {  const RegDone01Page({Key? key}) : super(key: key);  @override  State<RegDone01Page> createState() => _RegDone01PageState();}class _RegDone01PageState extends State<RegDone01Page> {  @override  Widget build(BuildContext context) {    return Scaffold(        backgroundColor: isDark(context)            ? PrsmColorsDark.primaryColor            : PrsmColorsLight.primaryColor,        body: SizedBox(            width: MediaQuery                .of(context)                .size                .width,            height: MediaQuery                .of(context)                .size                .height,            child: Center(                child: SpacedColumn(                  crossAxisAlignment: CrossAxisAlignment.center,                  verticalSpace: 10.0,                  children: [                    SizedBox(height: 300.h),                    Lottie.asset(                      'assets/lottie/sign_up_success.json',                      width: 500.w,                      height: 500.h,                      fit: BoxFit.cover,                    ),                    SizedBox(height: 100.h),                    SizedText(                      width: MediaQuery                          .of(context)                          .size                          .width - 200.w,                      textAlign: TextAlign.center,                      height: 500.h,                      softWrap: true,                      text:                      "${S(context).congratulation}\n ${S(context)                          .successLoginMsg}",                      textStyle: ThemeTextMedium.k18,                    ),                    SizedBox(height: 100.h),                    SizedBox(height: 100.h),                    PrimaryButton(                      buttonType: ButtonType.Ghost,                      buttonSize: ButtonSize.L3,                      btnTextStyle: ThemeTextMedium.k18                          .copyWith(color: ThemeColors.lightblue700),                      buttonText: S(context).goToLoginPage,                      onPressed: () {                        appRouter.replace(const Login01Route());                      },                    ),                  ],                ))));  }}