import 'package:auto_route/annotations.dart';import 'package:mehonot_admin/manager/models/Banner/banner_md.dart';import '../../template/template.dart';@RoutePage()class BannerDetailsPage extends StatefulWidget {  BannerModel banner;  BannerDetailsPage({Key? key, required this.banner}) : super(key: key);  @override  State<BannerDetailsPage> createState() => _BannerDetailsPageState();}class _BannerDetailsPageState extends State<BannerDetailsPage> {  @override  Widget build(BuildContext context) {    return Padding(        padding: EdgeInsets.all(20.w),        child: SingleChildScrollView(            child: SpacedColumn(                crossAxisAlignment: CrossAxisAlignment.start,                verticalSpace: 20,                children: [              SizedText(                  text: widget.banner.title, textStyle: ThemeTextMedium.k14),              Align(                  alignment: Alignment.center,                  child: ClipRRect(                      borderRadius: BorderRadius.circular(10),                      child: Image.network(                        widget.banner.image,                        width: 300,                        height: 200,                        fit: BoxFit.cover,                      ))),              SizedText(                  text: widget.banner.companyName,                  textStyle: ThemeTextMedium.k14),              SizedText(                  text: widget.banner.description,                  softWrap: true,                  textStyle: ThemeTextMedium.k14),              SizedBox(height: 20.h),              SizedText(                  text: "Email : ${widget.banner.email}",                  textStyle: ThemeTextRegular.k12),              SizedText(                  text: "Website : ${widget.banner.email}",                  textStyle: ThemeTextRegular.k12),            ])));  }}