import 'package:auto_route/annotations.dart';import 'package:auto_route/auto_route.dart';import 'package:mehonot_admin/manager/navigation/router.gr.dart';import '../../../manager/models/Banner/banner_md.dart';import '../../template/template.dart';import '../../widgets/containers/banner_container.dart';@RoutePage()class BannerHomeListPage extends StatefulWidget {  const BannerHomeListPage({Key? key}) : super(key: key);  @override  State<BannerHomeListPage> createState() => _BannerHomeListPageState();}class _BannerHomeListPageState extends State<BannerHomeListPage> {  @override  Widget build(BuildContext context) {    return StoreConnector<AppState, AppState>(        converter: (store) => store.state,        builder: (_, state) {          return Padding(              padding: EdgeInsets.all(20.w),              child: SingleChildScrollView(                  child: SpacedColumn(                crossAxisAlignment: CrossAxisAlignment.start,                children: [                  SpacedRow(                    mainAxisAlignment: MainAxisAlignment.spaceBetween,                    crossAxisAlignment: CrossAxisAlignment.center,                    children: [                      SizedText(                          text:                              "Home banners list : ${state.adsState.homeBanners.length - 1}",                          textStyle: ThemeTextRegular.k12),                      SizedBox(                          width: 180.w,                          child: PrimaryButton(                              buttonText: "Create",                              buttonSize: ButtonSize.L,                              buttonType: ButtonType.Link,                              btnTextStyle: ThemeTextRegular.k12,                              linkTypeBtnColor: ThemeColors.blue800,                              onPressed: () {                                context.pushRoute(BannerCreateRoute(                                  bannerType: "home",                                ));                              }))                    ],                  ),                  _buildBannerList(context, state),                ],              )));        });  }  Widget _buildBannerList(BuildContext context, AppState state) {    List<Widget> list = [];    List<BannerModel> homeBanners = state.adsState.homeBanners;    for (int i = 1; i < homeBanners.length; i++) {      list.add(BannerContainer(        onTap: () {          context.pushRoute(BannerDetailsRoute(              banner: BannerModel(            title: homeBanners[i].title,            companyName: homeBanners[i].companyName,            removeAt: homeBanners[i].removeAt,            postedById: homeBanners[i].postedById,            createdAt: homeBanners[i].createdAt,            id: homeBanners[i].id,            bannerType: homeBanners[i].bannerType,            phone: homeBanners[i].phone ?? "",            images: ["assets/images/png/apple_store_1.png"],            description: homeBanners[i].description ?? "",            email: homeBanners[i].email ?? "",            category: homeBanners[i].category ?? "",            website: homeBanners[i].website ?? '',          )));        },        onReqDecline: () {},        onReqAccept: () {},        onEdit: () {          context.pushRoute(BannerEditRoute(bannerMd:              BannerModel(            title: homeBanners[i].title,            companyName: homeBanners[i].companyName,            removeAt: homeBanners[i].removeAt,            postedById: homeBanners[i].postedById,            createdAt: homeBanners[i].createdAt,            id: homeBanners[i].id,            bannerType: homeBanners[i].bannerType,            phone: homeBanners[i].phone ?? "",            images: ["assets/images/png/apple_store_1.png"],            description: homeBanners[i].description ?? "",            email: homeBanners[i].email ?? "",            category: homeBanners[i].category ?? "",            website: homeBanners[i].website ?? '',          )));        },      ));    }    return SpacedColumn(      crossAxisAlignment: CrossAxisAlignment.center,      verticalSpace: 30.h,      children: list,    );  }}