import 'package:auto_route/annotations.dart';import 'package:auto_route/auto_route.dart';import 'package:mehonot_admin/manager/navigation/router.gr.dart';import '../../../manager/models/Banner/banner_md.dart';import '../../template/template.dart';import '../../widgets/containers/banner_container.dart';@RoutePage()class BannerSetListPage extends StatefulWidget {  const BannerSetListPage({Key? key}) : super(key: key);  @override  State<BannerSetListPage> createState() => _BannerSetListPageState();}class _BannerSetListPageState extends State<BannerSetListPage> {  @override  Widget build(BuildContext context) {    return StoreConnector<AppState, AppState>(        converter: (store) => store.state,        builder: (_, state) {          return Padding(              padding: EdgeInsets.all(20.w),              child: SingleChildScrollView(                  child: SpacedColumn(                crossAxisAlignment: CrossAxisAlignment.start,                children: [                  SpacedRow(                    mainAxisAlignment: MainAxisAlignment.spaceBetween,                    crossAxisAlignment: CrossAxisAlignment.center,                    children: [                      SizedText(                          text:                              "Setting banners list : ${state.adsState.settingBanner.length - 1}",                          textStyle: ThemeTextRegular.k12),                      SizedBox(                          width: 180.w,                          child: PrimaryButton(                              buttonText: "Create",                              buttonSize: ButtonSize.L,                              buttonType: ButtonType.Link,                              btnTextStyle: ThemeTextRegular.k12,                              linkTypeBtnColor: ThemeColors.blue800,                              onPressed: () {                                context.pushRoute(BannerCreateRoute(                                  bannerType: "setting",                                ));                              }))                    ],                  ),                  _buildBannerList(context, state),                ],              )));        });  }  Widget _buildBannerList(BuildContext context, AppState state) {    List<Widget> list = [];    List<BannerModel> settingBanners = state.adsState.settingBanner;    for (int i = 1; i < settingBanners.length; i++) {      list.add(BannerContainer(        onTap: () {          context.pushRoute(BannerDetailsRoute(              banner: BannerModel(            title: settingBanners[i].title,            companyName: settingBanners[i].companyName,            removeAt: settingBanners[i].removeAt,            postedById: settingBanners[i].postedById,            createdAt: settingBanners[i].createdAt,            id: settingBanners[i].id,            bannerType: settingBanners[i].bannerType,            phone: settingBanners[i].phone ?? "",            images: ["assets/images/png/apple_store_1.png"],            description: settingBanners[i].description ?? "",            email: settingBanners[i].email ?? "",            category: settingBanners[i].category ?? "",            website: settingBanners[i].website ?? '',          )));        },        onReqDecline: () {},        onReqAccept: () {},        onEdit: () {          context.pushRoute(const BannerEditRoute());        },      ));    }    return SpacedColumn(      crossAxisAlignment: CrossAxisAlignment.center,      verticalSpace: 30.h,      children: list,    );  }}