import '../../../template/template.dart';import '../../../utils/constants.dart';void openJobCategoriesPopup({  required BuildContext context,  required Function(List<String>) onSaveCat,  required List<String> selectedJobCategories,}) {  showDialog(    context: context,    builder: (BuildContext context) {      return StatefulBuilder(        builder: (BuildContext context, StateSetter setState) {          return AlertDialog(            title: SizedText(                text: 'Select Job Categories', textStyle: ThemeTextMedium.k18),            content: SizedBox(              height: 900.h,              width: 700.w,              child: SingleChildScrollView(                child: Column(                  mainAxisSize: MainAxisSize.min,                  children: List<Widget>.generate(                      Constants.jobCategoriesList.length, (index) {                    final category = Constants.jobCategoriesList[index];                    return CheckboxListTile(                      title: SizedText(                          text: category, textStyle: ThemeTextRegular.k14),                      value: selectedJobCategories.contains(category),                      onChanged: (value) {                        setState(() {                          if (selectedJobCategories.contains(category)) {                            selectedJobCategories.remove(category);                          } else {                            selectedJobCategories.add(category);                          }                        });                      },                    );                  }),                ),              ),            ),            actions: [              ElevatedButton(                child: const Text('Save'),                onPressed: () {                  onSaveCat(selectedJobCategories);                  Navigator.of(context).pop();                },              ),            ],          );        },      );    },  );}