import 'package:flutter/material.dart';import 'package:mehonot_admin/presentation/template/template.dart';class DefaultBottomSheet {  static Future<void> show({    double? heightFactor,    required String title,    VoidCallback? onSave,    required BuildContext context,    required Widget child,  }) async {    return showModalBottomSheet(      isScrollControlled: true,      enableDrag: true,      useRootNavigator: true,      context: context,      shape: const RoundedRectangleBorder(        borderRadius: BorderRadius.vertical(top: Radius.circular(24)),      ),      builder: (context) {        return SingleChildScrollView(          child: DefaultBottomSheetContainer(            title: title,            onSave: onSave,            child: child,          ),        );      },    );  }}class DefaultBottomSheetContainer extends StatelessWidget {  final String title;  final VoidCallback? onSave;  final Widget child;  const DefaultBottomSheetContainer({    Key? key,    required this.title,    this.onSave,    required this.child,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      padding: const EdgeInsets.all(24),      child: Column(        mainAxisSize: MainAxisSize.min,        children: [          SpacedRow(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              crossAxisAlignment: CrossAxisAlignment.center,              children: [                Text(title,                    style: const TextStyle(                      fontSize: 18,                      fontWeight: FontWeight.bold,                    )),                InkWell(                  child: const HeroIcon(HeroIcons.xMark),                  onTap: () => Navigator.of(context).pop(),                ),              ]),          const SizedBox(height: 20),          child,          const SizedBox(height: 20),          if (onSave != null)            ElevatedButton(              onPressed: onSave,              child: const Text('Save'),            ),          const SizedBox(height: 20),        ],      ),    );  }}