import '../../../manager/models/Feedback/feedback_md.dart';import '../../template/template.dart';import '../../utils/constants.dart';class FeedbackContainer extends StatelessWidget {  final VoidCallback? onDelete;  final FeedbackMd feedbackMd;  const FeedbackContainer({    Key? key,    this.onDelete,    required this.feedbackMd,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(        padding: EdgeInsets.all(24.w),        decoration: BoxDecoration(            color: ThemeColors.white,            borderRadius: BorderRadius.circular(12.r),            border: Border.all(color: ThemeColors.gray100, width: 1.w)),        child: SpacedColumn(          verticalSpace: 20,          children: [            SpacedRow(              crossAxisAlignment: CrossAxisAlignment.start,              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                SpacedColumn(                  crossAxisAlignment: CrossAxisAlignment.center,                  children: [                    Align(                        alignment: Alignment.centerLeft,                        child: SizedText(                            textAlign: TextAlign.start,                            text: "Rating: ${feedbackMd.rating}",                            textStyle: ThemeTextSemiBold                                .k12)), // SizedText( //     text: feedbackMd.email, //     textStyle: ThemeTextSemiBold.k10),                  ],                ),                if (onDelete != null)                  InkWell(                      onTap: onDelete,                      child: Container(                          decoration: BoxDecoration(                              color: ThemeColors.red500,                              borderRadius: BorderRadius.circular(8.r)),                          padding: EdgeInsets.all(5.w),                          child: HeroIcon(                            HeroIcons.xMark,                            size: 40.h,                            color: ThemeColors.white,                          ))),              ],            ),            SizedText(              text: feedbackMd.feedback,              textStyle: ThemeTextRegular.k10,              softWrap: true,            )          ],        ));  }}