import '../../template/template.dart';import '../../utils/constants.dart';class JobStatusChip extends StatelessWidget {  final String jobStatus;  const JobStatusChip({Key? key, required this.jobStatus}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      padding: EdgeInsets.symmetric(horizontal: 10.w, vertical: 5.h),      decoration: BoxDecoration(          color: isDark(context)              ? jobStatus.contains("pending")                  ? ThemeColors.yellow200                  : ThemeColors.green200              : jobStatus.contains("pending")                  ? ThemeColors.yellow700                  : ThemeColors.green700,          borderRadius: BorderRadius.circular(12.r)),      child: SizedText(        text: jobStatus,        textStyle: ThemeTextRegular.k10.copyWith(            color: isDark(context)                ? jobStatus.contains("pending")                    ? ThemeColors.yellow800                    : ThemeColors.green800                : jobStatus.contains("pending")                    ? ThemeColors.yellow100                    : ThemeColors.green100),      ),    );  }}