import '../template/template.dart';class EducationItem extends StatefulWidget {  String subjectName;  String startDate;  String? endDate;  String location;  String instituteName;  String degreeName;  bool showDivider;  VoidCallback? onDeleteEduItem;  EducationItem({    Key? key,    required this.subjectName,    required this.startDate,    this.endDate,    required this.location,    required this.instituteName,    required this.degreeName,    required this.showDivider,    this.onDeleteEduItem,  }) : super(key: key);  @override  State<EducationItem> createState() => _EducationItemState();}class _EducationItemState extends State<EducationItem> {  @override  Widget build(BuildContext context) {    return SizedBox(        child: SpacedColumn(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              SpacedRow(                  crossAxisAlignment: CrossAxisAlignment.center,                  mainAxisAlignment: MainAxisAlignment.spaceBetween,                  children: [                    SizedText(                        text: widget.subjectName,                        textStyle: ThemeTextSemiBold.k14),                    if (widget.onDeleteEduItem != null)                      InkWell(                        onTap: widget.onDeleteEduItem,                        child: const HeroIcon(                          HeroIcons.xMark,                          style: HeroIconStyle.solid,                        ),                      )                  ]),              SizedText(                  text: widget.degreeName, textStyle: ThemeTextRegular.k10),              SizedText(                  text: widget.instituteName, textStyle: ThemeTextSemiBold.k10),              SizedBox(height: 20.w),              SpacedColumn(verticalSpace: 20, children: [                SpacedRow(                    crossAxisAlignment: CrossAxisAlignment.center,                    horizontalSpace: 10,                    children: [                      Icon(Icons.location_on_outlined,                          color: isDark(context)                              ? ThemeColors.coolgray200                              : PrsmColorsDark.canvasColor,                          size: 24.h),                      SizedText(                          text: widget.location,                          textStyle: ThemeTextRegular.k10)                    ]),                SpacedRow(                    crossAxisAlignment: CrossAxisAlignment.center,                    horizontalSpace: 10,                    children: [                      Icon(Icons.calendar_month_outlined,                          color: isDark(context)                              ? ThemeColors.coolgray200                              : PrsmColorsDark.canvasColor,                          size: 24.h),                      SizedText(                          text: "${widget.startDate} - ${widget.endDate}",                          textStyle: ThemeTextRegular.k10),                    ]),              ]),              if (widget.showDivider) const Divider()            ]));  }}