import '../template/template.dart';class AlertOrigin extends StatelessWidget {  final String text;  final bool? isError;  const AlertOrigin({    required this.text,    this.isError = true,  });  @override  Widget build(BuildContext context) {    return Container(      height: 40,      decoration: BoxDecoration(        color: isError! ? ThemeColors.red50 : ThemeColors.green50,        borderRadius: BorderRadius.circular(6),      ),      padding: const EdgeInsets.symmetric(horizontal: 18),      child: SpacedRow(        horizontalSpace: 20,        crossAxisAlignment: CrossAxisAlignment.center,        children: [          HeroIcon(            //!Icons            isError! ? HeroIcons.xCircle : HeroIcons.checkCircle,            size: 24,            color: isError! ? ThemeColors.red400 : ThemeColors.emerald500,          ),          SizedText(            maxLines: 2,            text: text,            textStyle: ThemeTextRegular.k10.apply(              color: isError! ? ThemeColors.red800 : ThemeColors.emerald800,            ),          ),        ],      ),    );  }}