import 'package:intl/intl.dart';import '../../template/template.dart';void showCalendarModal({  required TextEditingController controller,  required BuildContext context,}) {  DateTime selectedDate = DateTime.now();  showDatePicker(    builder: (BuildContext context, Widget? child) {      return SizedBox(        width: 200.w,        height: 200.h,        child: Theme(          data: ThemeData.light().copyWith(            colorScheme: const ColorScheme.light(              onPrimary: ThemeColors.white,              surface: ThemeColors.white,              onSurface: ThemeColors.black,            ),            dialogBackgroundColor: ThemeColors.white,          ),          child: child!,        ),      );    },    context: context,    initialDate: selectedDate,    firstDate: DateTime(1900),    lastDate: DateTime.now(),  ).then((pickedDate) {    if (pickedDate != null) {      selectedDate = pickedDate;      controller.text = DateFormat('dd MMMM yyyy').format(selectedDate);    }  });}