import 'package:cached_network_image/cached_network_image.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:mehonot_admin/presentation/widgets/photo_widgets/shimmer_loader.dart';import 'package:shimmer/shimmer.dart';class CompanyLogoLoader extends StatefulWidget {  final String? companyLogoUrl;  final double? size;  const CompanyLogoLoader({    Key? key,    this.companyLogoUrl,    this.size,  }) : super(key: key);  @override  State<CompanyLogoLoader> createState() => _CompanyLogoLoaderState();}class _CompanyLogoLoaderState extends State<CompanyLogoLoader> {  @override  Widget build(BuildContext context) {    double size = widget.size ?? 150.w;    return Padding(      padding: widget.size != null          ? EdgeInsets.only(left: 0.w)          : EdgeInsets.only(left: 16.w),      child: ClipRRect(        borderRadius: BorderRadius.circular(8.r),        child:            (widget.companyLogoUrl != null && widget.companyLogoUrl!.isNotEmpty)                ? CachedNetworkImage(                    imageUrl: widget.companyLogoUrl!,                    width: size,                    height: size,                    fit: BoxFit.fill,                    placeholder: (context, url) => ShimmerImgLoader(                      width: size,                      height: size,                    ),                    errorWidget: (context, url, error) => Image.asset(                      'assets/images/png/error_img.png',                      fit: BoxFit.fill,                      width: size,                      height: size,                    ),                  )                : Image.asset(                    'assets/images/png/no_img.png',                    fit: BoxFit.fill,                    width: size,                    height: size,                  ),      ),    );  }}