import 'package:cached_network_image/cached_network_image.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:mehonot_admin/presentation/widgets/photo_widgets/shimmer_loader.dart';import '../../../manager/redux/sets/app_state.dart';import '../../template/theme/theme_additional.dart';import '../../template/theme/theme_color.dart';import '../../utils/common/helpers.dart';class SettingAdsLoader extends StatefulWidget {  final VoidCallback onTap;  const SettingAdsLoader({super.key, required this.onTap});  @override  State<SettingAdsLoader> createState() => _SettingAdsLoaderState();}class _SettingAdsLoaderState extends State<SettingAdsLoader> {  @override  Widget build(BuildContext context) {    double singleBoxWidth = MediaQuery.of(context).size.width / 3 - 40.w;    return InkWell(        onTap: widget.onTap,        child: Container(          height: 260.h,          width: singleBoxWidth * 2 + 30.w,          decoration: BoxDecoration(            color: isDark(context)                ? ThemeColors.bluegray600                : ThemeColors.lightblue50,            borderRadius: BorderRadius.circular(8.r),            boxShadow: ThemeShadows.shadowSm,          ),          child: ClipRRect(            borderRadius: BorderRadius.circular(22.r),            child: CachedNetworkImage(              imageUrl: appStore.state.adsState.settingBanner[0].image,              fit: BoxFit.cover,              errorWidget: (context, error, stackTrace) {                return const Center(                    child: Icon(Icons.error, color: Colors.red));              },              placeholder: (context, url) =>                  ShimmerImgLoader(width: 200.w, height: 200.h),            ),          ),        ));  }}