import 'package:flutter/material.dart';import 'package:shimmer/shimmer.dart';import '../../utils/common/helpers.dart';class ShimmerImgLoader extends StatefulWidget {  final double width;  final double height;  const ShimmerImgLoader(      {super.key, required this.width, required this.height});  @override  State<ShimmerImgLoader> createState() => _ShimmerImgLoaderState();}class _ShimmerImgLoaderState extends State<ShimmerImgLoader> {  @override  Widget build(BuildContext context) {    return Shimmer.fromColors(      baseColor: isDark(context) ? Colors.grey[800]! : Colors.grey[300]!,      highlightColor: isDark(context) ? Colors.grey[700]! : Colors.grey[100]!,      child: Container(        width: widget.width,        height: widget.height,        color: isDark(context) ? Colors.grey[800] : Colors.grey[300],      ),    );  }}