import '../../template/template.dart';class StatusAndRefreshWidget extends StatefulWidget {  final VoidCallback onTapRefresh;  final String statusAndDetails;  final VoidCallback? onTapCreate;  const StatusAndRefreshWidget({    required this.onTapRefresh,    required this.statusAndDetails,    this.onTapCreate,  });  @override  State<StatusAndRefreshWidget> createState() => _StatusAndRefreshWidgetState();}class _StatusAndRefreshWidgetState extends State<StatusAndRefreshWidget> {  @override  Widget build(BuildContext context) {    return Padding(      padding: EdgeInsets.symmetric(horizontal: 20.w),      child: SpacedRow(        crossAxisAlignment: CrossAxisAlignment.center,        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          SizedText(            text: widget.statusAndDetails,            textStyle:                ThemeTextSemiBold.k12.copyWith(color: ThemeColors.emerald700),          ),          SpacedRow(            crossAxisAlignment: CrossAxisAlignment.center,            children: [              Material(                child: InkWell(                  onTap: widget.onTapRefresh,                  child: SizedBox(                      width: 150.w,                      child: const HeroIcon(                        HeroIcons.arrowPath,                        color: ThemeColors.emerald900,                      )),                ),              ),              if (widget.onTapCreate != null)                Material(                  child: InkWell(                    onTap: widget.onTapCreate,                    child: SizedBox(                        width: 150.w,                        child: const HeroIcon(                          HeroIcons.plusCircle,                          color: ThemeColors.emerald900,                        )),                  ),                ),            ],          ),        ],      ),    );  }}